import { PokeApiService } from './../../service/poke-api.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { forkJoin } from 'rxjs';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.scss']
})
export class DetailsComponent implements OnInit {

  private urlPokemon: string = `https://pokeapi.co/api/v2/pokemon`;
  private urlName: string = `https://pokeapi.co/api/v2/pokemon-species`;

  public pokemon: any;
  public isLoading: boolean = false;
  public apiError: boolean = false;

  constructor(
    private activatedRoute: ActivatedRoute,
    private pokeService:  PokeApiService
  ) { }

  ngOnInit(): void {
    this.getpokemon();
  }
  public getpokemon(){
    const id = this.activatedRoute.snapshot.params['id'];
    const pokemon = this.pokeService.getPokemons(`${this.urlPokemon}/${id}`);
    const name = this.pokeService.getPokemons(`${this.urlName}/${id}`);

    return forkJoin([pokemon, name]).subscribe(
      res =>{
        this.pokemon = res;
        this.isLoading = true;
      },
      error => {

        this.apiError = true

      }
    )


  }
}
<!-- Header -->
<poke-header></poke-header>
<main>

  <ng-template [ngIf]="isLoading">

    <section class="pokemon__names">
      <h1 class="slideInLeft">{{pokemon[0].name}}</h1>
      <h2 class="slideInRight">{{pokemon[1].names[0].name}}</h2>
    </section>

    <section class="pokemon__status">
      <img
        [src]="pokemon[0].sprites.other.dream_world.front_default"
        [alt]="pokemon[0].name"
        class="slideInDown"
        >

      <article class="pokemon__status__statistics fadeIn">
        <section>
          <h3>Estatísticas</h3>

          <ol>
            <li *ngFor="let value of pokemon[0].stats">
                {{ value.stat.name }}

              <strong>
                {{ value.base_stat }}
              </strong>
            </li>
          </ol>
        </section>

        <a [routerLink]="['']" class="btn btn__secondary">
          Voltar
        </a>
      </article>
    </section>
  </ng-template>

  <img
   src="assets/error.png"
  alt="Deu erro no mostragem do pokemon"
  width="100%"
  *nfIf="apiError"
  >
</main>
